        self.GDBUserTxt    = wx.TextCtrl(self.DBSettingsTab, -1, self.config.get('go db','user'))
        self.GDBPasswrdTxt = wx.TextCtrl(self.DBSettingsTab, -1, self.config.get('go db','passwd'), style=wx.TE_PASSWORD)
